mapper.insert:
delete from sys_interfaces where id in

	#{item}

;
delete from sys_user_data where data_permission_id in(
	select id from sys_data_permission where interfaces_id not in(SELECT id from sys_interfaces)
);
delete from sys_role_data where data_permission_id in(
	select id from sys_data_permission where interfaces_id not in(SELECT id from sys_interfaces)
);
delete from sys_data_permission where interfaces_id not in(SELECT id from sys_interfaces);

mapper.select:
select id,name,url,base_url as baseUrl,method,times,unit,counts,data_permission as dataPermission,entity,history_times as historyTimes,request_data_secret as requestDataSecret,response_data_secret as responseDataSecret,remark,create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag from sys_interfaces
del_flag = 0 and name like CONCAT('%',#{name},'%') and url like CONCAT('%',#{url},'%') and base_url like CONCAT('%',#{baseUrl},'%') and method like CONCAT('%',#{method},'%')
ORDER BY name desc limit ${(currentPage-1)*pageSize},${pageSize}

