select.if:
SELECT module_id as moduleId,module_name as moduleName,page_url as pageUrl,api_url as apiUrl,parent_id as parentId, (select n.module_name from sys_module n where n.module_id = sys_module.parent_id and n.del_flag = 0) as parentName, fcode,level,orders,module_type as moduleType,alias,parent_alias as parentAlias,remark,icon, create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag from sys_module WHERE module_id in
#{id}
and del_flag = 0 ORDER BY level ASC,orders ASC

mapper.select:
select m.module_id as moduleId,m.parent_id as parentId,m.module_name as moduleName, (select n.module_name from sys_module n where n.module_id = m.parent_id and n.del_flag = 0) as parentName, m.page_url as pageUrl,m.api_url as apiUrl,m.module_type as moduleType,m.orders,m.alias,m.create_date as createDate from sys_module m
m.del_flag = 0 and m.module_name like CONCAT('%',#{moduleName},'%') and m.page_url like CONCAT('%',#{pageUrl},'%') and m.api_url like CONCAT('%',#{apiUrl},'%') and m.module_type = ${moduleType} and m.alias like CONCAT('%',#{alias},'%') and m.create_date >=#{createStartDate} and m.create_date <=#{createEndDate}
order by m.module_type asc,m.orders asc limit ${(currentPage-1)*pageSize},${pageSize}

select count(1) from sys_module m

	m.del_flag = 0
	and m.module_name like CONCAT('%',#{moduleName},'%')
	and m.page_url like CONCAT('%',#{pageUrl},'%')
	and m.api_url like CONCAT('%',#{apiUrl},'%')
	and m.module_type = ${moduleType}
	and m.alias like CONCAT('%',#{alias},'%')
	and m.create_date >=#{createStartDate}
	and m.create_date <=#{createEndDate}

mapper.delete:

delete from sys_user_module where module_id = #{_parameter};

delete from sys_role_module where module_id = #{_parameter};

delete from sys_module where module_id = #{_parameter};

mapper.select:
select role_id as roleId,role_name as roleName,parent_id as parentId,fcode,level,type,remark,
create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag 
from sys_role where role_id in(select DISTINCT role_id from sys_role_module where module_id = #{_parameter})

select user_id as userId,user_name as userName,password,person_name as personName,email,phone,portrait,parent_id as parentId,
fcode,level,remark,status,create_date as createDate,creator,update_date as updateDate,updater,del_flag as delFlag
from sys_user where user_id in(
	select distinct user_id from sys_user_role where role_id in(
		select role_id from sys_role where role_id in(
			select distinct role_id from sys_role_module where module_id = #{_parameter}
		)
	)
)
