mapper.select:
select u.user_id as userId,u.user_name as userName,u.person_name as personName,u.status,u.create_date as createDate, (select GROUP_CONCAT(r.role_name) from sys_role r where r.role_id in(select ur.role_id from sys_user_role ur where u.user_id = ur.user_id)) as roleName from sys_user u
u.del_flag = 0 and u.user_name like CONCAT('%',#{userName},'%') and u.person_name like CONCAT('%',#{personName},'%') and u.user_id in(select user_id from sys_user_role where role_id in(select role_id from sys_role where role_name like CONCAT('%',#{roleName},'%'))) and u.create_date >=#{createStartDate} and u.create_date <=#{createEndDate}
order by u.create_date desc limit ${(currentPage-1)*pageSize},${pageSize}

mapper.delete:

delete from sys_user_role where user_id = #{_parameter};

delete from sys_user_module where user_id = #{_parameter};

delete from sys_data_permission where id in(select distinct data_permission_id from sys_user_data where user_id = #{_parameter});

delete from sys_user_data where user_id = #{_parameter};

delete from sys_user where user_id = #{_parameter};

mapper.update:

delete from sys_user_role where user_id = #{userId};


	insert into sys_user_role(id,user_id,role_id,module_strategy,data_strategy) values

        (#{item.id},#{item.userId},#{item.roleId},#{item.moduleStrategy},#{item.dataStrategy})


delete from sys_user_module where user_id = #{userId};


	insert into sys_user_module(id,user_id,module_id) values

        (#{item.id},#{item.userId},#{item.moduleId})
